import { db, auth } from "../firebase";
import {ref, set} from "firebase/database";

//setFunction for userData
export  function writeUserData(name, email) {
    const userId = auth.currentUser.uid
     set(ref(db, 'Users/' + userId ), {
  
        user_email: email,
        user_id: userId,
        user_name: name,
        user_picture: "https://firebasestorage.googleapis.com/v0/b/login-3c524.appspot.com/o/default_image.jpg?alt=media&token=79fa1854-0679-4a41-a538-f45794d8e354",
        user_status: "online",
     });
  }
